        i0.ɵɵelement(529, "br");
        i0.ɵɵtext(530, "Im Zusammenhang mit dem Vertrieb von Batterien oder mit der Lieferung von Ger\u00E4ten, die Batterien enthalten, sind wir verpflichtet, Sie auf folgendes hinzuweisen:");
        i0.ɵɵelement(531, "br");
